import logo from "./logo.svg";
import "./App.css";
import { useCallback, useContext, useState } from "react";
import axios from "axios";
import { Context } from "./Context";

function CreateSession() {
  const [uuid, setUUID] = useContext(Context);

  const [inputParams, setInputParams] = useState({
    appName: "",
    clientId: "",
    clientSecret: "",
    storageAccountKey: "",
    tenantId: "",
  });

  function loadData() {
    // axios.get("http://localhost:8080/api/spark/read/taxidata").then((data) => {
    //   setTableData(data.data);
    // });
    console.log(inputParams);
  }

  return (
    <div className="App">
      <h1>Form</h1>
      <form onSubmit={() => loadData()}>
        <div className="form-element">
          <label>App Name</label>
          <input
            value={inputParams.appName}
            onChange={(event) =>
              setInputParams({ ...inputParams, appName: event.target.value })
            }
          ></input>
        </div>
        <div className="form-element">
          <label>Client ID</label>
          <input
            value={inputParams.clientId}
            onChange={(event) =>
              setInputParams({ ...inputParams, clientId: event.target.value })
            }
          ></input>
        </div>
        <div className="form-element">
          <label>Client Secret</label>
          <input
            value={inputParams.clientSecret}
            onChange={(event) =>
              setInputParams({
                ...inputParams,
                clientSecret: event.target.value,
              })
            }
          ></input>
        </div>
        <div className="form-element">
          <label>Storage Account Key</label>
          <input
            value={inputParams.storageAccountKey}
            onChange={(event) =>
              setInputParams({
                ...inputParams,
                storageAccountKey: event.target.value,
              })
            }
          ></input>
        </div>
        <div className="form-element">
          <label>Tenant Id</label>
          <input
            value={inputParams.tenantId}
            onChange={(event) =>
              setInputParams({ ...inputParams, tenantId: event.target.value })
            }
          ></input>
        </div>
        <div className="form-element js-center">
          <input
            className="display-button"
            type="submit"
            value="Create Session"
          ></input>
        </div>
      </form>
    </div>
  );
}

export default CreateSession;
