import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";
import Table from "./Table";
import axios from "axios";

function ReadData() {
  const [inputParams, setInputParams] = useState({
    storageAccountName: "",
    containerName: "",
    filePath: "",
    fileName: "",
    condition: "",
  });
  const [headers, setHeaders] = useState([]);
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    if (tableData.length > 0) {
      setHeaders(Object.keys(tableData[0]));
    }
  }, [tableData]);

  function loadData() {
    // axios.get("http://localhost:8080/api/spark/read/taxidata").then((data) => {
    //   setTableData(data.data);
    // });
    console.log(inputParams);
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>Azure Data UI</div>
      </header>
      <h1>Form</h1>
      <form onSubmit={() => loadData()}>
        <div className="form-element">
          <label>Storage Account Name</label>
          <input
            value={inputParams.storageAccountName}
            onChange={(event) =>
              setInputParams({
                ...inputParams,
                storageAccountName: event.target.value,
              })
            }
          ></input>
        </div>

        <div className="form-element">
          <label>Container Name</label>
          <input
            value={inputParams.containerName}
            onChange={(event) =>
              setInputParams({
                ...inputParams,
                containerName: event.target.value,
              })
            }
            x
          ></input>
        </div>
        <div className="form-element">
          <label>File Path</label>
          <input
            value={inputParams.filePath}
            onChange={(event) =>
              setInputParams({ ...inputParams, filePath: event.target.value })
            }
          ></input>
        </div>
        <div className="form-element">
          <label>File Name</label>
          <input
            value={inputParams.fileName}
            onChange={(event) =>
              setInputParams({ ...inputParams, fileName: event.target.value })
            }
          ></input>
        </div>
        <div className="form-element">
          <label>Condition</label>
          <input
            value={inputParams.condition}
            onChange={(event) =>
              setInputParams({ ...inputParams, condition: event.target.value })
            }
          ></input>
        </div>
        <div className="form-element js-center">
          <input
            className="display-button"
            type="submit"
            value="Display Data"
          ></input>
        </div>
      </form>

      <h1>Display table data</h1>
      <div className="table">
        <Table headers={headers} data={tableData}></Table>
      </div>
    </div>
  );
}

export default ReadData;
